CREATE TABLE count (id INTEGER NOT NULL,total INTEGER, errors INTEGER, good INTEGER, PRIMARY KEY(id));

CREATE TABLE freqs (it INTEGER NOT NULL, freq VARCHAR(32), freq_type VARCHAR(32), count INTEGER, PRIMARY KEY(it));

CREATE TABLE level (id INTEGER NOT NULL, level INTEGER, count INTEGER, PRIMARY KEY(id));

CREATE TABLE messages (id INTEGER NOT NULL, message_type VARCHAR(32) NOT NULL, msg_time INTEGER NOT NULL,station_id VARCHAR(32) NOT NULL, toaddr VARCHAR(32) NOT NULL, fromaddr VARCHAR(32) NOT NULL, depa VARCHAR(32) NOT NULL, dsta VARCHAR(32) NOT NULL, eta VARCHAR(32) NOT NULL, gtout VARCHAR(32) NOT NULL, gtin VARCHAR(32) NOT NULL, wloff VARCHAR(32) NOT NULL, wlin VARCHAR(32) NOT NULL, lat VARCHAR(32) NOT NULL, lon VARCHAR(32) NOT NULL, alt VARCHAR(32) NOT NULL, msg_text TEXT NOT NULL, tail VARCHAR(32) NOT NULL, flight VARCHAR(32) NOT NULL, icao VARCHAR(32) NOT NULL, freq VARCHAR(32) NOT NULL, ack VARCHAR(32) NOT NULL, mode VARCHAR(32) NOT NULL, label VARCHAR(32) NOT NULL, block_id VARCHAR(32) NOT NULL, msgno VARCHAR(32) NOT NULL, is_response VARCHAR(32) NOT NULL, is_onground VARCHAR(32) NOT NULL, error VARCHAR(32) NOT NULL, libacars TEXT NOT NULL,level VARCHAR(32) NOT NULL, PRIMARY KEY(id));

CREATE TABLE messages_saved (id INTEGER NOT NULL, message_type VARCHAR(32) NOT NULL, msg_time INTEGER NOT NULL, station_id VARCHAR(32) NOT NULL, toaddr VARCHAR(32) NOT NULL, fromaddr VARCHAR(32) NOT NULL, depa VARCHAR(32) NOT NULL, dsta VARCHAR(32) NOT NULL, eta VARCHAR(32) NOT NULL, gtout VARCHAR(32) NOT NULL, gtin VARCHAR(32) NOT NULL, wloff VARCHAR(32) NOT NULL, wlin VARCHAR(32) NOT NULL, lat VARCHAR(32) NOT NULL, lon VARCHAR(32) NOT NULL, alt VARCHAR(32) NOT NULL, msg_text TEXT NOT NULL,tail VARCHAR(32) NOT NULL, flight VARCHAR(32) NOT NULL, icao VARCHAR(32) NOT NULL, freq VARCHAR(32) NOT NULL, ack VARCHAR(32) NOT NULL, mode VARCHAR(32) NOT NULL, label VARCHAR(32) NOT NULL, block_id VARCHAR(32) NOT NULL, msgno VARCHAR(32) NOT NULL, is_response VARCHAR(32) NOT NULL, is_onground VARCHAR(32) NOT NULL, error VARCHAR(32) NOT NULL, libacars TEXT NOT NULL, level VARCHAR(32) NOT NULL, term VARCHAR(32) NOT NULL, type_of_match VARCHAR(32) NOT NULL, PRIMARY KEY(id));
